# This file is loaded by other scripts to centralize definition of common parameters
export LANCACHE_RAID="/dev/md/test:0" # output from mdadm --detail --scan
export LANCACHE_PARAMS="-e CACHE_MEM_SIZE=1000m -e NGINX_WORKER_PROCESSES=100 --restart unless-stopped --log-opt max-size=10m --log-opt max-file=3 --name lancache --detach -v /cache/data:/data/cache -v /cache/logs/:/data/logs -p 80:80 steamcache/monolithic:latest"